import { useState } from "react";
import { Link, useHistory, useParams } from "react-router-dom";
import { useEffect } from "react/cjs/react.development";
import employeeService from "../services/employee.service";
import DatePicker from 'react-datepicker';

const AddEmployee = () => {
    const[name, setName] = useState('');
    const[location, setLocation] = useState('');
    const[department, setDepartment] = useState('');
    const[dateOfJoining, setDateOfJoining] = useState(new Date());
    const[income, setIncome]=useState('');
    const[city , setCity] = useState('');
    const[profilePicture , setProfilePicture] = useState('');
    const history = useHistory();
    const {id} = useParams();

    const saveEmployee = (e) => {
        e.preventDefault();

        const employee = {name, location, department, city, income, dateOfJoining, profilePicture, id};
        if (id) {
            //update
            employeeService.update(employee)
                .then(response => {
                    console.log('Employee data updated successfully', response.data);
                    history.push('/');
                })
                .catch(error => {
                    console.log('Something went wrong', error);
                }) 
        } else {
            //create
            employeeService.create(employee)
            .then(response => {
                console.log("employee added successfully", response.data);
                history.push("/");
            })
            .catch(error => {
                console.log('something went wroing', error);
            })
        }
    }

    useEffect(() => {
        if (id) {
            employeeService.get(id)
                .then(employee => {
                    setName(employee.data.name);
                    setLocation(employee.data.location);
                    setDepartment(employee.data.department);
                    setIncome(employee.data.income);
                    setCity(employee.data.city);
                    setDateOfJoining(employee.data.dateOfJoining);
                    setProfilePicture(employee.data.profilePicture);
                })
                .catch(error => {
                    console.log('Something went wrong', error);
                })
        }
    }, [])
    return(
        <div className="container">
            <h3>Add Employee</h3>
            <hr/>
            <form>
                <div className="form-group">
                    <input 
                        type="text" 
                        className="form-control col-4"
                        id="name"
                        value={name}
                        onChange={(e) => setName(e.target.value)}
                        placeholder="Enter name"
                    />

                </div>
                <div className="form-group">
                    <input 
                        type="text" 
                        className="form-control col-4"
                        id="department"
                        value={department}
                        onChange={(e) => setDepartment(e.target.value)}
                        placeholder="Enter Department"
                    />
                </div>
                <div className="form-group">
                    <input 
                        type="text" 
                        className="form-control col-4"
                        id="location"
                        value={location}
                        onChange={(e) => setLocation(e.target.value)}
                        placeholder="Enter Location"
                    />
                </div>

                <div className="form-group">
                    <input 
                        type="text" 
                        className="form-control col-4"
                        id="income"
                        value={income}
                        onChange={(e) => setIncome(e.target.value)}
                        placeholder="Enter Income"
                    />
                </div>
<div className="form-group">
<select class="form-select form-select-lg mb-3" 
        aria-label=".form-select-lg example"
        onClick={(e)=>setDateOfJoining(e.target.value)}
        value={city}>
  <option selected>Select City</option>
  <option value="Belgaum">Belgaum</option>
  <option value="Pune">Pune</option>
  <option value="Bangalore">Bangalore</option>
        </select>

</div>



        <div  className="form-group" >            
        <DatePicker value={dateOfJoining} onChange={(date) => setDateOfJoining(date)} />
        </div>

        <div className="form-group">
                    <input 
                        type="text" 
                        className="form-control col-4"
                        id="profilepicture"
                        value={profilePicture}
                        onChange={(e) => setProfilePicture(e.target.value)}
                        placeholder="Provide DP Link "
                    />
                </div>

                <div >
                    <button onClick={(e) => saveEmployee(e)} className="btn btn-primary">Save</button>
                </div>
            </form>
            <hr/>
            <Link to="/">Back to List</Link>
        </div>
    )
}

export default AddEmployee;
